                        <script>
    function copyLink(itemId) {
        // Get the text from the <p> element with the unique ID
        var textToCopy = document.getElementById('link' + itemId).innerText;

        // Create a temporary textarea element to use for copying
        var tempTextArea = document.createElement('textarea');
        tempTextArea.value = textToCopy;
        document.body.appendChild(tempTextArea);
        tempTextArea.select();
        document.execCommand('copy');
        document.body.removeChild(tempTextArea);

        // Get the button to show the copied indication
        var button = document.getElementById('copyBtn' + itemId);
        
        // Create a notification element
        button.innerText = 'Copied';
        // button.parentNode.insertBefore(notification, button.nextSibling);

        // Show the notification

        // Hide the notification after 4 seconds
        setTimeout(function() {
            button.innerText = 'Copy Link';
        }, 1000);
    }
</script>
                
                        
